# Ударения в модуле russtress ищутся долго, поэтому хеширование значительно ускоряет генерацию
# Если отформатировать словарь, то количество строк кода увеличится примерно на 700, поверьте мне на слово


def stress(form):
    word = form.word
    if word.lower() in exceptions_not_normal:
        return exceptions_not_normal[word.lower()]
    elif form.normal_form.lower() in exceptions:
        return exceptions[form.normal_form.lower()]
    elif word in sb:
        return sb[word]
    accented_word = accent.put_stress(word)
    if '\'' not in accented_word:
        return 1

    dried = ''
    for el in accented_word:
        if el in 'аяоёуюыиэе' or el == '\'':
            dried += el
    sb[word] = dried.index('\'')
    return dried.index('\'')


def full():
    global sb
    for element in words.keys():
        ls = words[element]
        for word in ls:
            if word not in sb:
                sb[word] = stress(word)
